<template>
 <!-- clear:both -->
  <div id="hotbook" style="height:320px;width:100%;"></div>
 
</template>

<script>
import api from "../../api/library_api";
import echarts from "echarts";
import "echarts-wordcloud/dist/echarts-wordcloud.js";
import "echarts-wordcloud/dist/echarts-wordcloud.min.js";
require("echarts-wordcloud");

export default {
  data() {
    return {
      echartdata: []
    };
  },
  mounted() {
    this.init();
    //let mychart = this.$refs["hotbook"];
    echarts.init(this.testoptions());
    //窗口自适应
    const that = this;
    window.onresize = () => {
      //  根据窗口大小调整曲线大小
      let mychart = this.$echarts.init(document.getElementById("hotbook"));
      //mychart.style.width = '310px'
      let chart = this.$echarts.init(document.getElementById("borrowresult"));
      chart.resize();
      mychart.resize();
    };
    
  },
  methods: {
    init() {
      
      let mychart = this.$echarts.init(document.getElementById("hotbook"));
    
      let image = 'data:image/png;base64,'
      let maskImage = new Image();
      maskImage.src = image
      api.getcuc_hotbook_school().then(res => {
        for (let i = 0; i < res["中文名称"].length; i++) {
          let obj = new Object();
          obj.name = res["中文名称"][i];
          obj.value = res["借阅数目"][i];
          this.echartdata[i] = obj;
        }
        console.log(this.echartdata.length);

        

        mychart.setOption({
          show: true,
          title: {
            text: "",
            subtext: "",
            x: "center"
          },
          grid: {
            left: "1%",
            right: "1%",
            bottom: "3%",
            containLabel: true
          },
          backgroundColor: "#fff",
          tooltip: {
            show: false
          },
          series: [
            {
              drawOutOfBound:true,
              type: "wordCloud",
              //shape:'star',//////////
              maskImage:maskImage,//////////
              gridSize: 1,
              sizeRange: [1, 25],
              rotationRange: [-45, 0, 45, 90],
              drawOutOfBound: false,///////

              textStyle: {
                emphasis:{
                    fontFamily:'Microsoft YaHei',
                },
                normal: {
                  fontStyle:'oblique',
                  textBorderColor:'#fff',
                  // width:'20%',
                  // height:'20%',
                  //textShadowOffsetX: 20,
                  color: function() {
                    return (
                      "rgb(" +
                      Math.round(Math.random() * 255) +
                      ", " +
                      Math.round(Math.random() * 255) +
                      ", " +
                      Math.round(Math.random() * 255) +
                      ")"
                    );
                  }
                }
              },
              left: "center",
              top: "center",

              right: null,
              bottom: null,

              data: this.echartdata
            }
          ]
        });
        mychart.resize();
      });
    },
    testoptions() {}
  }
};
</script>